<input type='hidden' id='assigned_user_id' name='assigned_user_id' value='1'>
<input type='hidden' id='bool_id' name='bool_id' value='check_returned_c;disputed_c;claims_inability_to_pay_c;phone_disconnected_c;mail_returned_c;no_response_c;'>
<input type='hidden' id='moduleDir' name='moduleDir' value='Leads'></form>
<script type='text/javascript'>
    function submit_form() {
        if (typeof(validateCaptchaAndSubmit) != 'undefined') {
            validateCaptchaAndSubmit();
        } else {
            check_webtolead_fields();
            //document.WebToLeadForm.submit();
        }
    }

    function check_webtolead_fields() {
        if (document.getElementById('bool_id') != null) {
            var reqs = document.getElementById('bool_id').value;
            bools = reqs.substring(0, reqs.lastIndexOf(';'));
            var bool_fields = new Array();
            var bool_fields = bools.split(';');
            nbr_fields = bool_fields.length;
            for (var i = 0; i < nbr_fields; i++) {
                if (document.getElementById(bool_fields[i]).value == 'on') {
                    document.getElementById(bool_fields[i]).value = 1;
                } else {
                    document.getElementById(bool_fields[i]).value = 0;
                }
            }
        }
    }
</script></body></html>